import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import apiService from '../services/apiService';
import './Profile.css';

const Profile = () => {
  const { token, user } = useAuth();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    dob: '',
    preferences: {},
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [idProofFile, setIdProofFile] = useState(null);

  useEffect(() => {
    if (!token) {
      localStorage.setItem('redirectAfterLogin', '/profile');
      navigate('/login');
      return;
    }
    fetchProfile();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [token, navigate]);

  const fetchProfile = async () => {
    try {
      setLoading(true);
      const response = await apiService.getProfile(token);
      setProfile(response.data);
      setFormData({
        name: response.data.name || '',
        address: response.data.address || '',
        dob: response.data.dob || '',
        preferences: response.data.preferences || {},
      });
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to load profile');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    setIdProofFile(e.target.files[0]);
  };

  const handleSaveProfile = async (e) => {
    e.preventDefault();
    try {
      setError('');
      setSuccessMessage('');

      const updateData = { ...formData };
      if (idProofFile) {
        updateData.idProofFile = idProofFile.name;
      }

      const response = await apiService.updateProfile(token, updateData);
      setProfile(response.data.user);
      setSuccessMessage(response.data.message);
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update profile');
    }
  };

  if (loading) return <div className="loading">Loading profile...</div>;

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h1>My Profile</h1>
        <p>Manage your personal information and verification</p>
      </div>

      {error && <div className="alert error">{error}</div>}
      {successMessage && <div className="alert success">{successMessage}</div>}

      <div className="profile-content">
        <div className="profile-sidebar-card">
          <div className="profile-avatar-large">
            {profile?.username?.charAt(0).toUpperCase() || user?.username?.charAt(0).toUpperCase() || 'U'}
          </div>
          <h2 className="profile-username">{profile?.username || user?.username}</h2>
          <p className="profile-email">{profile?.email || user?.email}</p>

          <div className="verification-status">
            <div className={`status-badge ${profile?.pendingKYC ? 'pending' : 'verified'}`}>
              {profile?.pendingKYC ? '⏳ Pending Approval' : '✅ Verified Account'}
            </div>
          </div>

          <div className="profile-stats">
            <div className="stat-item">
              <span className="stat-value">142</span>
              <span className="stat-label">Rides</span>
            </div>
            <div className="stat-item">
              <span className="stat-value">4.95</span>
              <span className="stat-label">Rating</span>
            </div>
            <div className="stat-item">
              <span className="stat-value">2</span>
              <span className="stat-label">Years</span>
            </div>
          </div>
        </div>

        <div className="profile-form-card">
          <div className="card-header">
            <h3>Personal Details</h3>
            <button className="edit-mode-btn">
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15.232 5.232l3.536 3.536m-2.036-5.036a2.5 2.5 0 113.536 3.536L6.5 21.036H3v-3.572L16.732 3.732z" />
              </svg>
              Edit
            </button>
          </div>

          <form onSubmit={handleSaveProfile} className="profile-form">
            <div className="form-group-half">
              <div className="form-group">
                <label>Full Name</label>
                <div className="input-wrapper">
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                  </svg>
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    placeholder="Enter your full name"
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Date of Birth</label>
                <div className="input-wrapper">
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                  </svg>
                  <input
                    type="date"
                    name="dob"
                    value={formData.dob}
                    onChange={handleInputChange}
                  />
                </div>
              </div>
            </div>

            <div className="form-group">
              <label>Address</label>
              <div className="input-wrapper textarea-wrapper">
                <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                </svg>
                <textarea
                  name="address"
                  value={formData.address}
                  onChange={handleInputChange}
                  placeholder="Enter your full address"
                  rows="3"
                />
              </div>
            </div>

            <div className="form-group">
              <label>ID Proof (KYC Verification)</label>
              <div className="file-upload-wrapper">
                <input
                  type="file"
                  id="id-proof"
                  onChange={handleFileChange}
                  accept=".pdf,.jpg,.jpeg,.png"
                  className="file-input"
                />
                <label htmlFor="id-proof" className="file-label">
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
                  </svg>
                  <span>{idProofFile ? idProofFile.name : 'Click to upload ID proof'}</span>
                </label>
              </div>
              <p className="hint">Upload a PDF or image (JPG/PNG)</p>
            </div>

            <div className="form-actions">
              <div className="disclaimer-text">
                <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                Changes to sensitive details will require re-verification.
              </div>
              <button type="submit" className="save-btn">
                Save Changes
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Profile;
