// API service to communicate with backend
import axios from 'axios';

const API_BASE = 'http://localhost:5000/api';

const apiService = {
  // Auth
  register: (email, username, password, name) =>
    axios.post(`${API_BASE}/auth/register`, { email, username, password, name }),
  
  login: (email, password) =>
    axios.post(`${API_BASE}/auth/login`, { email, password }),
  
  // Wallet
  getWallet: (token) =>
    axios.get(`${API_BASE}/wallet`, {
      headers: { Authorization: `Bearer ${token}` }
    }),
  
  addFunds: (token) =>
    axios.post(`${API_BASE}/wallet/add-funds`, {}, {
      headers: { Authorization: `Bearer ${token}` }
    }),
  
  // Promo Codes
  getPromos: (token) =>
    axios.get(`${API_BASE}/promos`, {
      headers: { Authorization: `Bearer ${token}` }
    }),
  
  addPromo: (token, code) =>
    axios.post(`${API_BASE}/promos/add`, { code }, {
      headers: { Authorization: `Bearer ${token}` }
    }),
  
  removePromo: (token, promoId) =>
    axios.delete(`${API_BASE}/promos/${promoId}`, {
      headers: { Authorization: `Bearer ${token}` }
    }),
  
  // Profile
  getProfile: (token) =>
    axios.get(`${API_BASE}/profile`, {
      headers: { Authorization: `Bearer ${token}` }
    }),
  
  updateProfile: (token, profileData) =>
    axios.put(`${API_BASE}/profile`, profileData, {
      headers: { Authorization: `Bearer ${token}` }
    }),
};

export default apiService;
