import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import apiService from '../services/apiService';
import './Wallet.css';

const Wallet = () => {
  const { token } = useAuth();
  const navigate = useNavigate();
  const [wallet, setWallet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!token) {
      localStorage.setItem('redirectAfterLogin', '/wallet');
      navigate('/login');
      return;
    }
    fetchWallet();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [token, navigate]);

  const fetchWallet = async () => {
    try {
      setLoading(true);
      const response = await apiService.getWallet(token);
      setWallet(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to load wallet');
    } finally {
      setLoading(false);
    }
  };

  const handleAddFunds = async () => {
    try {
      await apiService.addFunds(token);
      // Refresh wallet after adding funds
      await fetchWallet();
      alert('✅ $100 added to your wallet!');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to add funds');
    }
  };

  if (loading) return <div className="loading">Loading wallet...</div>;
  if (error) return <div className="error">{error}</div>;

  const [transactions] = useState([
    { id: 1, type: 'credit', amount: 100, description: 'Added funds', date: '2026-02-03', time: '14:30' },
    { id: 2, type: 'debit', amount: 45.50, description: 'Ride payment', date: '2026-02-03', time: '14:00' },
    { id: 3, type: 'credit', amount: 100, description: 'Added funds', date: '2026-02-02', time: '10:15' },
    { id: 4, type: 'debit', amount: 28.00, description: 'Ride payment', date: '2026-02-02', time: '09:30' }
  ]);

  return (
    <div className="wallet-container">
      <div className="wallet-header">
        <h1>My Wallet</h1>
        <p>Manage your funds and transactions</p>
      </div>

      <div className="wallet-balance-card">
        <div className="balance-icon">
          <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
          </svg>
        </div>
        <div className="balance-info">
          <div className="balance-label">Current Balance</div>
          {wallet && (
            <div className="balance-amount">${wallet.balance.toFixed(2)}</div>
          )}
          <div className="balance-id">Wallet ID: {wallet?.id}</div>
        </div>
        <button className="add-funds-btn" onClick={handleAddFunds}>
          <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
          </svg>
          Add Funds
        </button>
      </div>

      <div className="wallet-actions-grid">
        <button className="action-card">
          <div className="action-icon send-icon">
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
            </svg>
          </div>
          <div className="action-label">Send Money</div>
        </button>
        <button className="action-card">
          <div className="action-icon request-icon">
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16V4m0 0L3 8m4-4l4 4m6 0v12m0 0l4-4m-4 4l-4-4" />
            </svg>
          </div>
          <div className="action-label">Request Money</div>
        </button>
        <button className="action-card">
          <div className="action-icon history-icon">
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
          </div>
          <div className="action-label">Transaction History</div>
        </button>
      </div>

      <div className="transactions-section">
        <div className="section-header">
          <h2>Recent Transactions</h2>
          <button className="view-all-btn">View All</button>
        </div>
        <div className="transactions-list">
          {transactions.map(transaction => (
            <div key={transaction.id} className="transaction-item">
              <div className={`transaction-icon ${transaction.type}`}>
                {transaction.type === 'credit' ? (
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
                  </svg>
                ) : (
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20 12H4" />
                  </svg>
                )}
              </div>
              <div className="transaction-details">
                <div className="transaction-description">{transaction.description}</div>
                <div className="transaction-datetime">
                  {transaction.date} • {transaction.time}
                </div>
              </div>
              <div className={`transaction-amount ${transaction.type}`}>
                {transaction.type === 'credit' ? '+' : '-'}${transaction.amount.toFixed(2)}
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="wallet-info-card">
        <div className="info-icon">ℹ️</div>
        <div className="info-content">
          <strong>Note:</strong> Sprint 1 wallet is simplified. Only fixed $100 additions are supported.
          Created on {wallet && new Date(wallet.createdAt).toLocaleDateString()}.
        </div>
      </div>
    </div>
  );
};

export default Wallet;
