import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Header.css';

const Header = () => {
  const { user, token, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const navigateTo = (path) => {
    if (token) {
      navigate(path);
    } else {
      navigate('/login');
    }
  };

  return (
    <header className="header">
      <div className="header-container">
        <div className="logo">
          <h1>üíº SEP Project</h1>
        </div>
        
        <nav className="nav-menu">
          {token ? (
            <>
              <span className="user-info">Welcome, {user?.username || 'User'}!</span>
              <button className="nav-btn" onClick={() => navigateTo('/wallet')}>
                üëõ Wallet
              </button>
              <button className="nav-btn" onClick={() => navigateTo('/promo')}>
                üéÅ Promo Codes
              </button>
              <button className="nav-btn" onClick={() => navigateTo('/profile')}>
                üë§ Profile
              </button>
              <button className="nav-btn logout-btn" onClick={handleLogout}>
                üö™ Logout
              </button>
            </>
          ) : (
            <>
              <button className="nav-btn" onClick={() => navigate('/login')}>
                üîê Login
              </button>
              <button className="nav-btn" onClick={() => navigate('/register')}>
                üìù Register
              </button>
            </>
          )}
        </nav>
      </div>
    </header>
  );
};

export default Header;
