import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import apiService from '../services/apiService';
import './Wallet.css';

const Wallet = () => {
  const { token } = useAuth();
  const navigate = useNavigate();
  const [wallet, setWallet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!token) {
      localStorage.setItem('redirectAfterLogin', '/wallet');
      navigate('/login');
      return;
    }
    fetchWallet();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [token, navigate]);

  const fetchWallet = async () => {
    try {
      setLoading(true);
      const response = await apiService.getWallet(token);
      setWallet(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to load wallet');
    } finally {
      setLoading(false);
    }
  };

  const handleAddFunds = async () => {
    try {
      await apiService.addFunds(token);
      // Refresh wallet after adding funds
      await fetchWallet();
      alert('âœ… $100 added to your wallet!');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to add funds');
    }
  };

  if (loading) return <div className="loading">Loading wallet...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="wallet-container">
      <div className="wallet-card">
        <h2>ðŸ‘› My Wallet</h2>
        {wallet && (
          <>
            <div className="wallet-info">
              <div className="wallet-item">
                <span className="label">Wallet ID:</span>
                <span className="value">{wallet.id}</span>
              </div>
              <div className="wallet-item">
                <span className="label">Balance:</span>
                <span className="balance">${wallet.balance.toFixed(2)}</span>
              </div>
              <div className="wallet-item">
                <span className="label">Created:</span>
                <span className="value">{new Date(wallet.createdAt).toLocaleDateString()}</span>
              </div>
            </div>
            <button className="add-funds-btn" onClick={handleAddFunds}>
              âž• Add $100 to Wallet
            </button>
            <div className="disclaimer">
              <p><strong>Note:</strong> Sprint 1 wallet is simplified. Only fixed $100 additions are supported.</p>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Wallet;
