import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import apiService from '../services/apiService';
import './Promo.css';

const Promo = () => {
  const { token } = useAuth();
  const navigate = useNavigate();
  const [promos, setPromos] = useState([]);
  const [newCode, setNewCode] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    if (!token) {
      localStorage.setItem('redirectAfterLogin', '/promo');
      navigate('/login');
      return;
    }
    fetchPromos();
  }, [token, navigate]);

  const fetchPromos = async () => {
    try {
      setLoading(true);
      const response = await apiService.getPromos(token);
      setPromos(response.data.promos || []);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to load promos');
    } finally {
      setLoading(false);
    }
  };

  const handleAddPromo = async (e) => {
    e.preventDefault();
    if (!newCode.trim()) {
      setError('Please enter a promotion code');
      return;
    }

    try {
      setError('');
      setSuccessMessage('');
      const response = await apiService.addPromo(token, newCode);
      setPromos([...promos, response.data.promo]);
      setNewCode('');
      setSuccessMessage('‚úÖ Promotion added successfully!');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to add promo');
    }
  };

  const handleRemovePromo = async (promoId) => {
    try {
      await apiService.removePromo(token, promoId);
      setPromos(promos.filter(p => p.id !== promoId));
      setSuccessMessage('‚úÖ Promotion removed!');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to remove promo');
    }
  };

  if (loading) return <div className="loading">Loading promos...</div>;

  return (
    <div className="promo-container">
      <div className="promo-card">
        <h2>üéÅ Promotion Codes</h2>

        {error && <div className="error-message">{error}</div>}
        {successMessage && <div className="success-message">{successMessage}</div>}

        <form onSubmit={handleAddPromo} className="add-promo-form">
          <div className="form-group">
            <label>Add New Promotion Code:</label>
            <input
              type="text"
              value={newCode}
              onChange={(e) => setNewCode(e.target.value.toUpperCase())}
              placeholder="e.g., WELCOME100"
            />
          </div>
          <button type="submit">‚ûï Add Promotion Code</button>
          <p className="hint">Valid codes: WELCOME10, SAVE20, SUMMER50</p>
        </form>

        <div className="promos-list">
          <h3>Your Promotions ({promos.length})</h3>
          {promos.length === 0 ? (
            <p className="no-promos">No promotion codes added yet.</p>
          ) : (
            promos.map((promo) => (
              <div key={promo.id} className="promo-item">
                <div className="promo-info">
                  <span className="promo-code">{promo.code}</span>
                  <span className="promo-desc">{promo.description}</span>
                  <span className="promo-date">
                    Added: {new Date(promo.addedAt).toLocaleDateString()}
                  </span>
                </div>
                <button
                  className="remove-btn"
                  onClick={() => handleRemovePromo(promo.id)}
                >
                  üóëÔ∏è Remove
                </button>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Promo;
