import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import apiService from '../services/apiService';
import './Promo.css';

const Promo = () => {
  const { token } = useAuth();
  const navigate = useNavigate();
  const [promos, setPromos] = useState([]);
  const [newCode, setNewCode] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const [availableCoupons] = useState([
    { code: 'WELCOME50', desc: '50% off your first ride', expiry: 'Expires in 7 days' },
    { code: 'RIDE10', desc: '$10 off on ride > $50', expiry: 'Expires in 2 days' },
    { code: 'SUMMER20', desc: '20% off all rides', expiry: 'Valid till Aug 31' }
  ]);

  useEffect(() => {
    if (!token) {
      localStorage.setItem('redirectAfterLogin', '/promo');
      navigate('/login');
      return;
    }
    fetchPromos();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [token, navigate]);

  const fetchPromos = async () => {
    try {
      setLoading(true);
      const response = await apiService.getPromos(token);
      setPromos(response.data.promos || []);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to load promos');
    } finally {
      setLoading(false);
    }
  };

  const handleAddPromo = async (e) => {
    e.preventDefault();
    if (!newCode.trim()) {
      setError('Please enter a promotion code');
      return;
    }

    try {
      setError('');
      setSuccessMessage('');
      const response = await apiService.addPromo(token, newCode);
      setPromos([...promos, response.data.promo]);
      setNewCode('');
      setSuccessMessage('‚úÖ Promotion added successfully!');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to add promo');
    }
  };

  const handleRemovePromo = async (promoId) => {
    try {
      await apiService.removePromo(token, promoId);
      setPromos(promos.filter(p => p.id !== promoId));
      setSuccessMessage('‚úÖ Promotion removed!');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to remove promo');
    }
  };

  const copyToClipboard = (code) => {
    setNewCode(code);
    navigator.clipboard.writeText(code);
    setSuccessMessage('Code copied and ready to add!');
    setTimeout(() => setSuccessMessage(''), 2000);
  };

  if (loading) return <div className="loading">Loading promos...</div>;

  return (
    <div className="promo-container">
      <div className="promo-header">
        <h1>Promo Codes</h1>
        <p>Manage your discounts and rewards</p>
      </div>

      {error && <div className="alert error">{error}</div>}
      {successMessage && <div className="alert success">{successMessage}</div>}

      <div className="promo-grid">
        <div className="promo-column-left">
          <div className="card add-promo-card">
            <h2>Add New Code</h2>
            <form onSubmit={handleAddPromo} className="add-promo-form">
              <div className="form-group">
                <input
                  type="text"
                  value={newCode}
                  onChange={(e) => setNewCode(e.target.value.toUpperCase())}
                  placeholder="Enter promo code"
                  className="promo-input"
                />
                <button type="submit" className="add-btn">
                  Apply
                </button>
              </div>
            </form>
          </div>

          <div className="card available-coupons-card">
            <h2>Available Coupons</h2>
            <div className="coupons-list">
              {availableCoupons.map((coupon, index) => (
                <div key={index} className="coupon-item" onClick={() => copyToClipboard(coupon.code)}>
                  <div className="coupon-left">
                    <div className="coupon-code">{coupon.code}</div>
                    <div className="coupon-desc">{coupon.desc}</div>
                  </div>
                  <div className="coupon-right">
                    <div className="coupon-expiry">{coupon.expiry}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        <div className="promo-column-right">
          <div className="card active-promos-card">
            <h2>Your Active Promotions</h2>
            {promos.length === 0 ? (
              <div className="empty-promos">
                <div className="empty-icon">üéÅ</div>
                <h3>No active promotions</h3>
                <p>Add a code to start saving!</p>
              </div>
            ) : (
              <div className="active-promos-list">
                {promos.map((promo) => (
                  <div key={promo.id} className="active-promo-item">
                    <div className="active-promo-icon">
                      <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 7h.01M7 3h5c.512 0 1.024.195 1.414.586l7 7a2 2 0 010 2.828l-7 7a2 2 0 01-2.828 0l-7-7A1.994 1.994 0 013 12V7a4 4 0 014-4z" />
                      </svg>
                    </div>
                    <div className="active-promo-info">
                      <div className="active-promo-code">{promo.code}</div>
                      <div className="active-promo-desc">{promo.description}</div>
                      <div className="active-promo-date"> Added: {new Date(promo.addedAt).toLocaleDateString()}</div>
                    </div>
                    <button
                      className="remove-promo-btn"
                      onClick={() => handleRemovePromo(promo.id)}
                      title="Remove"
                    >
                      <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                      </svg>
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Promo;
