import React, { useState } from 'react';
import './Notifications.css';

const Notifications = () => {
    const [filter, setFilter] = useState('all');

    const [notifications, setNotifications] = useState([
        {
            id: 1,
            type: 'ride',
            title: 'Ride Completed',
            message: 'Your ride from Downtown Plaza to Airport Terminal 2 has been completed successfully.',
            time: '5 minutes ago',
            read: false,
            icon: 'ðŸš—'
        },
        {
            id: 2,
            type: 'payment',
            title: 'Payment Received',
            message: 'Payment of $45.50 has been credited to your wallet.',
            time: '10 minutes ago',
            read: false,
            icon: 'ðŸ’°'
        },
        {
            id: 3,
            type: 'promo',
            title: 'New Promo Code Available',
            message: 'Use code RIDE20 to get 20% off on your next 3 rides!',
            time: '1 hour ago',
            read: false,
            icon: 'ðŸŽ'
        },
        {
            id: 4,
            type: 'rating',
            title: 'You Received a 5-Star Rating',
            message: 'A passenger rated you 5 stars! Keep up the great service.',
            time: '2 hours ago',
            read: true,
            icon: 'â­'
        },
        {
            id: 5,
            type: 'system',
            title: 'App Update Available',
            message: 'A new version of CabGo is available. Update now for the latest features.',
            time: '3 hours ago',
            read: true,
            icon: 'ðŸ””'
        },
        {
            id: 6,
            type: 'ride',
            title: 'Ride Request',
            message: 'New ride request from Central Station to Tech Park.',
            time: '5 hours ago',
            read: true,
            icon: 'ðŸš—'
        },
        {
            id: 7,
            type: 'payment',
            title: 'Weekly Earnings Summary',
            message: 'You earned $542.80 this week. Great job!',
            time: '1 day ago',
            read: true,
            icon: 'ðŸ’°'
        },
        {
            id: 8,
            type: 'system',
            title: 'Profile Verification Complete',
            message: 'Your profile has been successfully verified.',
            time: '2 days ago',
            read: true,
            icon: 'âœ…'
        }
    ]);

    const filteredNotifications = filter === 'all'
        ? notifications
        : filter === 'unread'
            ? notifications.filter(n => !n.read)
            : notifications.filter(n => n.type === filter);

    const unreadCount = notifications.filter(n => !n.read).length;

    const markAsRead = (id) => {
        setNotifications(notifications.map(n =>
            n.id === id ? { ...n, read: true } : n
        ));
    };

    const markAllAsRead = () => {
        setNotifications(notifications.map(n => ({ ...n, read: true })));
    };

    const deleteNotification = (id) => {
        setNotifications(notifications.filter(n => n.id !== id));
    };

    return (
        <div className="notifications-container">
            <div className="notifications-header">
                <div>
                    <h1>Notifications</h1>
                    <p>{unreadCount} unread notification{unreadCount !== 1 ? 's' : ''}</p>
                </div>
                {unreadCount > 0 && (
                    <button className="mark-all-read-btn" onClick={markAllAsRead}>
                        Mark All as Read
                    </button>
                )}
            </div>

            <div className="notifications-filters">
                <button
                    className={filter === 'all' ? 'active' : ''}
                    onClick={() => setFilter('all')}
                >
                    All
                </button>
                <button
                    className={filter === 'unread' ? 'active' : ''}
                    onClick={() => setFilter('unread')}
                >
                    Unread {unreadCount > 0 && `(${unreadCount})`}
                </button>
                <button
                    className={filter === 'ride' ? 'active' : ''}
                    onClick={() => setFilter('ride')}
                >
                    Rides
                </button>
                <button
                    className={filter === 'payment' ? 'active' : ''}
                    onClick={() => setFilter('payment')}
                >
                    Payments
                </button>
                <button
                    className={filter === 'promo' ? 'active' : ''}
                    onClick={() => setFilter('promo')}
                >
                    Promos
                </button>
                <button
                    className={filter === 'system' ? 'active' : ''}
                    onClick={() => setFilter('system')}
                >
                    System
                </button>
            </div>

            <div className="notifications-list">
                {filteredNotifications.length === 0 ? (
                    <div className="empty-state">
                        <div className="empty-icon">ðŸ“­</div>
                        <h3>No notifications</h3>
                        <p>You're all caught up!</p>
                    </div>
                ) : (
                    filteredNotifications.map((notification) => (
                        <div
                            key={notification.id}
                            className={`notification-item ${!notification.read ? 'unread' : ''}`}
                        >
                            <div className="notification-icon">
                                {notification.icon}
                            </div>
                            <div className="notification-content">
                                <div className="notification-title">{notification.title}</div>
                                <div className="notification-message">{notification.message}</div>
                                <div className="notification-time">{notification.time}</div>
                            </div>
                            <div className="notification-actions">
                                {!notification.read && (
                                    <button
                                        className="action-btn mark-read-btn"
                                        onClick={() => markAsRead(notification.id)}
                                        title="Mark as read"
                                    >
                                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                                        </svg>
                                    </button>
                                )}
                                <button
                                    className="action-btn delete-btn"
                                    onClick={() => deleteNotification(notification.id)}
                                    title="Delete"
                                >
                                    <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                                    </svg>
                                </button>
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

export default Notifications;
